import React from 'react';
class App extends React.Component {
    state={
        hour:0,
        minut:0,
        secund : 0,
        dis_btn : false,
        interval:'',
        saveinter :[],
        dis_btnone:true
    }
  Starwatch=()=>{
   let i= setInterval(() => {
        const {secund, minut ,hour} =this.state
       this.setState({
        secund: this.state.secund+1
       }) 
       if(secund===59){
        this.setState({
            minut : minut +1,
            secund:0 
        })
       }
       if(minut === 59){
        this.setState({
            hour : hour + 1,
            minut:0,
            secund:0
        })
       }
    }, 0.1);
    this.setState({
        dis_btnone:false,
        dis_btn : true,
         interval : i
    })
  }

  Stopwatch =()=>{
    clearInterval(this.state.interval)    
    this.setState({
        dis_btn : false
    })
  }

  SavedInteval =()=>{
    const {minut, hour, secund ,saveinter} = this.state
    saveinter.push(hour + ':'+minut +':' +secund)
    this.setState({
        saveinter
    })
  }

  clearInterval = () => {
    const { horu, minut, secund, interval, dis_btn } = this.state;
    clearInterval(interval)
    this.setState({
      minut:0,
      hour :0,
      secund:0,
      saveinter:[],
      dis_btn: false

    });
  };


    render() {
        const {hour, minut, secund,dis_btn, dis_btnone}= this.state
        return (
         <div class="container">
           <div className="row">
            <div className="col-md-6 offset-3 mt-4">
                <div className="card">
                    <div className="card-header">
                        <h1 className="text-center">
                            StopWatch
                        </h1> 
                    </div>
                    <div className="card-body  ">

                        <button>hour 2</button>
                        <h1 className='text-center position-relative'> {hour}:{minut}:{secund} 
                        <button className='postion- absolute top-0 start-0 w-1'>i</button></h1>
                    </div>
                    <div className="card-footer  ">
                      <div className="row">
                      <div className="col-md-3 text-center">
                            <button className="btn btn-info" onClick={this.Starwatch} disabled={dis_btn}> Start</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-warning" onClick={this.Stopwatch} > Stop</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-success" disabled={dis_btnone} onClick={this.SavedInteval}> Interval</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-danger" onClick={this.clearInterval}> Clear</button>
                        </div>
                      </div>
                      <div className="row">
                        {
                            this.state.saveinter.map((item ,index ) =>(
                                <h2 key={index}>{item}</h2>
                            ))
                        }
                      </div>
                    </div>
                </div>
            </div>
           </div>
         </div>

        );
    }
}



export default App;
