import React from 'react';
class App extends React.Component {
    state={
        hour:0,
        minut:0,
        secund : 0,
        dis_btn : false
    }
  Starwatch=()=>{
    setInterval(() => {
        const {secund, minut ,hour} =this.state
       this.setState({
        secund: this.state.secund+1
       }) 
       if(secund===59){
        this.setState({
            minut : minut +1,
            secund:0 
        })
       }
       if(minut === 59){
        hour=hour +1
        minut=0;
        secund=0
       }
    }, 0.1);
    this.setState({
        dis_btn : true
    })

  }

    render() {
        const {hour, minut, secund,dis_btn}= this.state
        return (
         <div class="container">
           <div className="row">
            <div className="col-md-6 offset-3 mt-4">
                <div className="card">
                    <div className="card-header">
                        <h1 className="text-center">
                            StopWatch
                        </h1>
                    </div>
                    <div className="card-body">
                        <h2 className='text-center '> {hour}:{minut}:{secund} </h2>
                    </div>
                    <div className="card-footer  ">
                      <div className="row">
                      <div className="col-md-3 text-center">
                            <button className="btn btn-info" onClick={this.Starwatch} disabled={dis_btn}> Start</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-warning" > Stop</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-success"> Interval</button>
                        </div>
                        <div className="col-md-3 text-center">
                            <button className="btn btn-danger"> Clear</button>
                        </div>
                      </div>
                    </div>
                </div>
            </div>
           </div>
         </div>

        );
    }
}



export default App;
